import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Button, Grid } from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';

const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    paddingLeft: theme.spacing(12),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  input: {
    display: 'none',
  },

}));

function Upload() {
  const classes = useStyles();


  function handleUpload(e) {
    const data = new FormData()

    var models = Array.from(e.target.files)

    for (let m of models) {

      data.append('file', m)

      axios.post("http://localhost:3002/upload",data).then(response => {
        // server answer
      }).catch(e => {
        console.log(e);
      });

      console.log(m);
    }
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={2}   container
        direction="row"
        justify="center"
        lignItems="center">

        <Grid item xs={3} direction="column">
          <div>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}>
              Upload File
              <input
                  type="file"
                  multiple
                  accept=".pdf,.obj,.stl"
                  className={classes.input}
                  id="Upload-button-file"
                  onChange={handleUpload}

              />
          </Button>
          </div>
            <Paper className={classes.paper}>Uploaded files</Paper>
        </Grid>
        <Grid item xs={9}>
          <Paper className={classes.paper}>canvas</Paper>
        </Grid>
      </Grid>
    </div>




  );
}

export default Upload;
