import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {  Button, Grid, Checkbox } from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import LaunchIcon from '@material-ui/icons/Launch';
import PrintIcon from '@material-ui/icons/Print';
import IconButton from '@material-ui/core/IconButton';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle'
import model from '../image.jpg';
import Preview from './Preview'
import SendIcon from '@material-ui/icons/Send';






const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(12),
    marginBottom: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  input: {
    display: 'none',
  },
  table: {
    margin: theme.spacing(2),
    maxHeight: 270,
  },
  paper: {
    margin: theme.spacing(2),
  },
  model: {
    marginLeft:theme.spacing(15),
  },
  buttons: {
    marginLeft:theme.spacing(30),
  },

}));




function Upload() {

  const [models, setModels] = useState([]);
  const [open, setOpen] = React.useState(false);
  const [count, setCount] = useState(0);

  const classes = useStyles();


  const handleClose = () => {
    setOpen(false);
  };
  const addItem = (file) => {
    setModels([
      ...models,
      {
        id: models.length,
        value: file.name,
        sel: false,
        file: file,
      }
    ]);
  };

  function handleCheckboxClick(event, id){
    event.stopPropagation();


      if (models[id].sel === false) {
          models[id].sel = true;
          setCount(count + 1)

      } else if (models[id].sel === true) {
          models[id].sel = false; 
          setCount(count - 1)
      }

  };

  function handleUpload(e) {
    var upModels = Array.from(e.target.files)

    for (let m of upModels) {
      console.log(m)
      addItem(m.name)
    }
  }

  function handleServer(){
    const data = new FormData()

    for (let m of models) {
      data.append('file', m)
      axios.post("http://localhost:3002/upload",data).then(response => {
        // server answer
      }).catch(e => {
        console.log(e);
      });

    }
  }

  function handlerDisplay(){
    if(count === 1){



    }else{
      setOpen(true);
    }
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={2} direction="row">
        <Grid container item xs={3} direction="column">
          <div>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}className={classes.upload}>
              Upload File
              <input
                  type="file"
                  accept=".pdf,.obj,.stl"
                  className={classes.input}
                  id="Upload-button-file"
                  onChange={handleUpload.bind(this)}
              />
          </Button>
          </div>
          <TableContainer component={Paper} className={classes.table}>
            <Table  aria-label="simple table" stickyHeader>
              <TableHead>
                <TableRow>
                <TableCell></TableCell>
                  <TableCell>Uploaded files</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {models.map(model =>
                  <TableRow key={model.id}>
                    <TableCell padding="checkbox">
                      <Checkbox className={classes.check} onClick={event => handleCheckboxClick(event, model.id)}/>
                    </TableCell>
                    <TableCell>{model.value}</TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <Grid container item direction="row" className={classes.buttons} >
          <IconButton color="secondary" aria-label="Display" onClick={handleServer}>
             <SendIcon fontSize="large"/>
          </IconButton>
          <IconButton color="primary" aria-label="Display" onClick={handlerDisplay}>
             <LaunchIcon fontSize="large"/>
          </IconButton>
            <Dialog
              open={open}
              onClose={handleClose}
              aria-labelledby="alert-dialog-title"
              aria-describedby="alert-dialog-description"
            >
              <DialogTitle id="alert-dialog-title">{"Incorrect number of models"}</DialogTitle>
              <DialogContent>
                <DialogContentText id="alert-dialog-description">
                  You need to select only one model to display.
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button onClick={handleClose} color="primary">
                  ok
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
        </Grid>
        <Grid item xs={9} >
          <div className={classes.model}>
            <Preview/>
          </div>
        </Grid>
      </Grid>
    </div>
  );
}



export default Upload;
