import React, { useState,useEffect } from 'react';
import {STLViewer} from 'react-stl-obj-viewer';
import {  Button,IconButton, Collapse } from '@material-ui/core';
import BackspaceIcon from '@material-ui/icons/Backspace';



function STLDisplay(props){

	const [aux, setaux] = useState([]);
	const [button, setbutton] = useState(false);
	const [backColor, ] = useState("#b0b0b0");

	useEffect(() => {   
		setaux([props.file])
		setbutton(true)
		props.setUnavailable()
	},[props.file]);

	function deleteid(){
		props.setAvailable()
		setaux([])
		setbutton(false)
	}

return(
	<div>
		<Collapse in={button}>
			<IconButton color="primary" aria-label="Display" onClick={deleteid}>
			<BackspaceIcon fontSize="medium"/>
			</IconButton>
		</Collapse>

		{aux.map(auxModel=>
			<STLViewer
			key={auxModel.id+256}
			width={800}
			height={380}
			sceneClassName="test-scene"
			file={auxModel}
			className="stl"
			modelColor="#FF0000"
			backgroundColor={backColor}
			/> 
		)
		}

	</div>
)};

export default STLDisplay;
