import React from 'react';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import CloseIcon from '@material-ui/icons/Close';
//percentaje
import CircularProgress from '@material-ui/core/CircularProgress';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import Collapse from '@material-ui/core/Collapse';
import { IconButton, Grid, Button} from '@material-ui/core';
import Preview from './Preview';
import {useState} from 'react'
import axios from 'axios';
import Iframe from 'react-iframe'



const useStyles = makeStyles(theme => ({
  paper: {
    marginLeft: theme.spacing(20),
    marginTop: theme.spacing(2),
    marginRight: theme.spacing(5),
  },
  label1: {
    marginTop: theme.spacing(5),
  },
  margin: {
    margin: theme.spacing(1),
  },
  label2: {
    marginTop: theme.spacing(3),
  },
  closeButton: {
    marginLeft: theme.spacing(2),
    marginTop: theme.spacing(4)
  },
  iframe: {
    border: 0,
    width: 250, /*or any other size*/
    height: 300, /*or any other size*/
    overflow: hidden,
  },
  printStatus: {
    marginLeft:theme.spacing(5),
  },



}));
function State(props) {

  const classes = useStyles();

  const [status,setStatus] = useState(false);
  const [enableBut,setEnableBut] = useState(false);


  if(props.state.state==="Printing"){
    if(status===false){
      setStatus(true)
    }
  }

  if(props.state.state==="Paused"){
    if(enableBut===false){
      setStatus(true)
    }
  }


  function handlerCancel(){

    setEnableBut(true)
    axios.get("http://mini3dfactory.duckdns.org:3004/api/communication/cancel").then(response => {
      // server answer
    }).catch(e => {
      console.log(e);
    });
  }

  function handlerPause(){

    setEnableBut(true)
    axios.get("http://mini3dfactory.duckdns.org:3004/api/communication/pause").then(response => {
      // server answer
    }).catch(e => {
      console.log(e);
    });
  }


  return (
    <div>
      <Paper className={classes.paper}>
     <Collapse in={status}>
          <Grid container spacing={2}>
            <Grid item xs={2} >
              <div className={classes.model} > 
                {props.model.map(auxModel=>
                    <Preview size={70} file={auxModel} key={1024} margin={10}/>
                )}
              </div>
            </Grid>
            <Grid item sm container>
              <Grid item xs={5} container direction="column" spacing={2}>
                <Grid item xs>
                  <Typography gutterBottom variant={"h5"}>
                    State: {props.state.state}
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                    File: {props.state.name}
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                    Size: {props.state.size} MB
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                    User: {props.state.user}
                  </Typography>
                </Grid>
                <Grid container direction="row">
                  <Button variant="contained"  size="small" color="default" disabled={enableBut} className={classes.margin} onClick={handlerCancel}>
                    Cancel
                  </Button>
                  <Button variant="contained" size="small" color="default" disabled={enableBut} className={classes.margin} onClick={handlerPause}>
                    {props.state.state === "Paused"
                      ? "Resume"
                      : "Pause"
                    }
                  </Button>
                </Grid>

              </Grid>
              <Grid item xs={5} container direction="column" spacing={2} alignItems={"center"} >
                <Grid item xs className={classes.label1}>
                  <Typography variant="body2" gutterBottom >
                    Filament: {props.state.filaLength}m/{props.state.filaVol}cm<sup>3</sup>
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                    Print time left: {props.state.printTimeLeft} min
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                    Print time: {props.state.printTime}
                  </Typography>
                </Grid>
              </Grid>
              <Grid item container xs={1} direction={"column"} alignContent={"center"}>
                <Typography variant="subtitle1">
                  {props.state.price}â‚¬
                </Typography>
                <Grid item className={classes.label2} >
                  <Box position="relative" display="inline-flex">
                    <CircularProgress variant="static" value={props.state.percent} />
                    <Box
                      top={0}
                      left={0}
                      bottom={0}
                      right={0}
                      position="absolute"
                      display="flex"
                      alignItems="center"
                      justifyContent="center"
                    >
                      <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                        props.state.percent,
                      )}%`}</Typography>
                    </Box>
                  </Box>
                </Grid>
              </Grid>
              <Grid item xs={1} className={classes.closeButton}>
                <IconButton color="default" aria-label="Delete">
                  <CloseIcon/>
                </IconButton> 
              </Grid>
            </Grid>
          </Grid>
          <iframe width="500" src="http://mini3dfactory.duckdns.org:3008/webcam/?action=stream" scrolling="no" ></iframe>
        </Collapse>
      </Paper>
      </div>
  );
}

export default State;
