	import React, { useState,useEffect } from 'react';
	import {STLViewer} from 'react-stl-obj-viewer';
	import {IconButton, Collapse} from '@material-ui/core';
	import BackspaceIcon from '@material-ui/icons/Backspace';
	import { GithubPicker } from 'react-color';


	function STLDisplay(props){

		const [aux, setaux] = useState([]);
		const [button, setbutton] = useState(false);
		const [backColor,] = useState("#b0b0b0");
		const [modelColor, setmodelColor ] = useState("#0a43f5");

		useEffect(() => {   
			setaux([props.file])
			setbutton(true)
			props.setUnavailable()
			// eslint-disable-next-line react-hooks/exhaustive-deps
		},[props.file]);

		useEffect(() => {  
			setaux([props.file])
		},[modelColor,props]);


		function deleteid(){
			props.setAvailable()
			setaux([])
			setbutton(false)
		}

		function changeHandler(color){
			setmodelColor(color.hex)
			setmodelColor(color.hex)
			console.log(color.hex)
		}


	return(
		<div>

			<GithubPicker
        		color={ modelColor}
        		onChange={ changeHandler }
      		/>

			<Collapse in={button}>
				<IconButton color="primary" aria-label="Display" onClick={deleteid}>
				<BackspaceIcon fontSize="large"/>
				</IconButton>
			</Collapse>

			{console.log(modelColor)}

			{aux.map(auxModel=>
				<STLViewer
				key={auxModel.id+256}
				width={800}
				height={380}
				sceneClassName="test-scene"
				file={auxModel}
				className="stl"
				modelColor={modelColor}
				backgroundColor={backColor}
				/> 
			)
			}


		</div>
	)};
	export default STLDisplay;
