import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {  Button, Grid } from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import LaunchIcon from '@material-ui/icons/Launch';
import PrintIcon from '@material-ui/icons/Print';
import IconButton from '@material-ui/core/IconButton';
import model from '../image.jpg';




const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(12),
    marginBottom: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  input: {
    display: 'none',
  },
  table: {
    margin: theme.spacing(2),
  },
  paper: {
    margin: theme.spacing(2),
  },
  model: {
    marginLeft:theme.spacing(15),
  },
  buttons: {
    marginLeft:theme.spacing(30),
  },
}));


function Upload() {

  const [models, setModels] = useState([]);
  const classes = useStyles();



  const addItem = (name) => {
    setModels([
      ...models,
      {
        id: models.length,
        value: name
      }
    ]);
  };



  function handleUpload(e) {
    const data = new FormData()



    var models = Array.from(e.target.files)

    for (let m of models) {

      data.append('file', m)
      addItem(m)
      axios.post("http://localhost:3002/upload",data).then(response => {
        // server answer
      }).catch(e => {
        console.log(e);
      });


      console.log(m);
    }
  }

  function handlePrint(){
    //code for send models to print and optimize
  }

  function handlerDisplay(){
    //code for display selected model on the screen
  }



  return (
    <div className={classes.root}>
      <Grid container spacing={2} direction="row">
        <Grid container  xs={3} direction="column">
          <div>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}className={classes.upload}>
              Upload File
              <input
                  type="file"
                  multiple
                  accept=".pdf,.obj,.stl"
                  className={classes.input}
                  id="Upload-button-file"
                  onChange={handleUpload.bind(this)}
              />
          </Button>
          </div>
          <TableContainer component={Paper} className={classes.table}>
            <Table  aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Uploaded files</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {models.map(model =>
                  <TableRow key={model.id} >
                    <TableCell>{model.value}</TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <div className={classes.buttons}>
          <IconButton aria-label="SendToPrint" onClick={handlePrint}>
             <PrintIcon fontSize="large"/>
          </IconButton>
          <IconButton color="primary" aria-label="Display" onClick={handlerDisplay}>
             <LaunchIcon fontSize="large"/>
          </IconButton>

          </div>
        </Grid>
        <Grid item xs={9} >
          <div className={classes.model}>
            <img src={model} alt="model"/>
          </div>
        </Grid>
      </Grid>
    </div>




  );
}



export default Upload;
