import React, { useState , useEffect } from 'react';
import axios from 'axios';
  //general
import { makeStyles } from '@material-ui/core/styles';
import { IconButton, Grid, Checkbox} from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
  //table
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
  //icons
import DashboardIcon from '@material-ui/icons/Dashboard';
import PrintIcon from '@material-ui/icons/Print';
import DeleteIcon from '@material-ui/icons/Delete';

import CircularProgress from '@material-ui/core/CircularProgress';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';

import Preview from './Preview';





const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(12),
    marginBottom: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  table: {
    margin: theme.spacing(2),
    maxHeight: 350,
    width: 520

  },
  paper: {
    margin: theme.spacing(2),
  },
  model: {
    marginLeft:theme.spacing(5),
  },
  buttons: {
    marginLeft:theme.spacing(55),
  },
  numbers: {
    width:50
  },

}));

function FileList() {

  const [models, setModels] = useState([]);
  const [count, setCount] = useState(0);
  const [state, setState] = useState("Down");
  const [percent, setPercent] = useState(0)
  //const [del, setDel] = useState(0);

  const classes = useStyles();

  useEffect(() => { 
    handlerCharge()
  },[]);

  useEffect(() => {
    const interval = setInterval(() => {
      console.log("ola")
      setPercent(percent+1)
      axios.get("http://mini3dfactory.duckdns.org:3004/api/communication/state").then(response => { 
        setState(response.data.state)
      }).catch(e => {
        console.log(e);
      });
    }, 3000);
    return () => clearInterval(interval);
  }, []);



  function handlerDelete(id){

    axios.delete("http://mini3dfactory.duckdns.org:3002/api/models/delete/" + models[id].value).then(response => {
      handlerCharge()
    }).catch(e => {
      console.log(e);
    });

  };


  function handleCheckboxClick(event, id){
    event.stopPropagation();

      if (models[id].sel === false) {
          models[id].sel = true;
          setCount(count + 1)

      } else if (models[id].sel === true) {
          models[id].sel = false; 
          setCount(count - 1)
      }
  };

  function handlerNumber(n,id){
    if(n !== ""){
      console.log(n)
      models[id].number = n
    }
  }

  function handlerCancelar(){

    axios.get("http://mini3dfactory.duckdns.org:3004/api/communication/cancel").then(response => {
      // server answer
    }).catch(e => {
      console.log(e);
    });

  }


  //function to optimize the .stl models 
  function handlerOptimize(){
    var arrOfModels = []

    for (let m of models) {
      if (m.sel === true){
        arrOfModels.push({name:m.value , number:m.number})
      }
    }

    axios.post("http://mini3dfactory.duckdns.org:3003/api/optimize",arrOfModels).then(response => {
      handlerCharge()
    }).catch(e => {
      console.log(e);
    });

  }

  function handlerPrint(){

    var fullName 

    for (let m of models) {
      if (m.sel === true){
        fullName = m.value
      }
    }

    var filename = fullName.slice(0,-4)

    axios.post("http://mini3dfactory.duckdns.org:3002/api/models/printupload/"+filename).then(response => {
      // server answer
    }).catch(e => {
      console.log(e);
    });
  }

  //function to read all the models that are uploaded on the server
  function handlerCharge(){

    axios.get("http://mini3dfactory.duckdns.org:3002/api/models").then(function (response){ 

          var namelist = response.data;
          var items = [];

            setModels([])

          let counter = 0;

          namelist.forEach( nl => {

            axios.get("http://mini3dfactory.duckdns.org:3002/api/models/Uploaded_models/"+nl,{responseType: 'blob'}).then(function (response){ 

              const f = new Blob([response.data]);
              var file = new File([f], nl, {type: "application/sla", lastModified: Date.now()});

                items.push({
                  value: file.name,
                  sel: false,
                  file: file,
                  number: "1",
                  id: counter
                })
                counter++

                if(counter === namelist.length){
                  setModels(items)
                }
              })
              .catch(function (error) {
                console.log(error);
              });
            })
      })
      .catch(function (error) {
        console.log(error);
      });

  }

  return (
    <div className={classes.root}>
      <Grid container spacing={2} direction="row">
        <Grid container item xs={4} direction="column">
          <TableContainer component={Paper} className={classes.table}>
            <Table  aria-label="simple table" stickyHeader>
              <TableHead>
                <TableRow>
                <TableCell></TableCell>
                <TableCell></TableCell>
                <TableCell>Files on server</TableCell>
                <TableCell></TableCell>
                <TableCell></TableCell>
                </TableRow>
              </TableHead>

              <TableBody>
                {models.map(model =>
                  <TableRow key={model.id}>
                    <TableCell padding="checkbox">
                      <Checkbox color="primary" checked={model.sel} onClick={event => handleCheckboxClick(event, model.id)}/>
                    </TableCell>
                    <TableCell>  
                      {model.sel ? 
                        <TextField type={"number"} className={classes.numbers} onChange={event => handlerNumber(event.target.value,model.id)} defaultValue="1" id="number" size="small"/>
                                                : null
                      }
                    </TableCell>
                    <TableCell className={classes.name}>{model.value}</TableCell>
                    <TableCell>  
                      <Preview size={50} file={model.file}/>
                    </TableCell>
                    <TableCell>  
                      {model.sel ? 
                        <IconButton color="secondary" aria-label="Delete" onClick={() => handlerDelete(model.id)}>
                          <DeleteIcon fontSize="large"/>
                        </IconButton> 
                                                : null
                      }

                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>

          <Grid container item direction="row" className={classes.buttons} >
            <IconButton color="primary" disabled={!count} aria-label="Optimize" onClick={handlerOptimize}>
              <DashboardIcon fontSize="large"/>
            </IconButton>

            <IconButton  color="primary" disabled={!count} aria-label="Optimize" onClick={handlerPrint}>
              <PrintIcon fontSize="large"/>
            </IconButton>

          </Grid>
          <Grid item xs={8} >


          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...percent} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                percent,
              )}%`}</Typography>
      </Box>
    </Box>



          </Grid>

        </Grid>
    </Grid>
    </div>
  );
}

export default FileList;
