import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Button, Grid } from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';



const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    margin: theme.spacing(8),
  },
  fullWidth: {
    width: '100%',
  },
  input: {
    display: 'none',
  },

}));

function _generateRows(){
    return this.state.data.map(el => {
      return <TableRow>
                <TableRowColumn>{el.a}</TableRowColumn>
                <TableRowColumn>{el.b}</TableRowColumn>
                <TableRowColumn>{el.c}</TableRowColumn>
            </TableRow>
      })
}

function Upload() {
  const classes = useStyles();
  var arr = ["object.stl","object2.stl"];

  function handleUpload(e) {
    const data = new FormData()

    arr.push("hola.stl")
    var models = Array.from(e.target.files)

    for (let m of models) {

      data.append('file', m)

      axios.post("http://localhost:3002/upload",data).then(response => {
        // server answer
      }).catch(e => {
        console.log(e);
      });


      console.log(m);
    }
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={2}   container
        direction="row"
        justify="center"
        lignItems="center">

        <Grid item xs={3} direction="column">
          <div>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}className={classes.upload}>
              Upload File
              <input
                  type="file"
                  multiple
                  accept=".pdf,.obj,.stl"
                  className={classes.input}
                  id="Upload-button-file"
                  onChange={handleUpload}
              />
          </Button>
          </div>
          <Typography variant="h6" className={classes.title}>
              Uploaded files
          </Typography>
          <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Uploaded files</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {this._generateRows}
        </TableBody>
      </Table>
    </TableContainer>
        </Grid>
        <Grid item xs={9}>
          <Paper className={classes.paper}>canvas</Paper>
        </Grid>
      </Grid>
    </div>




  );
}

export default Upload;
