import React, { useState , useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Checkbox} from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Preview from './Preview';
import STLDisplay from './STLDisplay';





const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(12),
    marginBottom: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  table: {
    margin: theme.spacing(2),
    maxHeight: 270,
  },
  paper: {
    margin: theme.spacing(2),
  },
  model: {
    marginLeft:theme.spacing(5),
  },
  buttons: {
    marginLeft:theme.spacing(40),
  },


}));

function FileList() {

  const [models, setModels] = useState([]);
  const [count, setCount] = useState(0);
  const [aux, setaux] = useState([]);

  const classes = useStyles();


  useEffect(() => { 
    handlerCharge()
  },[]);



  function handlerDisplay(){
    if(count === 1){
      for (let m of models) {
        if(m.sel === true){
            setaux([m.file]);
        }
      }
    }else{
      setOpen(true);
    }
  }

  function handleCheckboxClick(event, id){
    event.stopPropagation();

      if (models[id].sel === false) {
          models[id].sel = true;
          setCount(count + 1)

      } else if (models[id].sel === true) {
          models[id].sel = false; 
          setCount(count - 1)
      }
  };

  function handlerCharge(){

    axios.get("http://localhost:3002/req").then(function (response){ 

          var namelist = response.data;
          var items = [];

          let counter = 0;

          namelist.forEach( nl => {

            axios.get("http://localhost:3002/filelist/"+nl,{responseType: 'blob'}).then(function (response){ 

              const f = new Blob([response.data]);
              var file = new File([f], nl, {type: "application/sla", lastModified: Date.now()});

                items.push({
                  value: file.name,
                  sel: false,
                  file: file,
                  id: counter
                })
                counter++

                if(counter === namelist.length){
                  setModels(items)
                }
              })
              .catch(function (error) {
                console.log(error);
              });
            })
      })
      .catch(function (error) {
        console.log(error);
      });

}

  return (
    <div className={classes.root}>
      <Grid container spacing={2} direction="row">
        <Grid container item xs={4} direction="column">
          <TableContainer component={Paper} className={classes.table}>
            <Table  aria-label="simple table" stickyHeader>
              <TableHead>
                <TableRow>
                <TableCell></TableCell>
                  <TableCell>Files Uploaded on the server</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              </TableHead>

              <TableBody>
                {models.map(model =>
                  <TableRow key={model.id}>
                    <TableCell padding="checkbox">
                      <Checkbox className={classes.check} onClick={event => handleCheckboxClick(event, model.id)}/>
                    </TableCell>
                    <TableCell>{model.value}</TableCell>
                    <TableCell>  
                      <Preview size={50} file={model.file}/>
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
        <div className={classes.model} >  
            {aux.map(auxModel=>
                <STLDisplay file={auxModel}  size={400} key={auxModel.id+840}/>
            )
            }
        </div>
    </Grid>
    </div>
  );
}

export default FileList;
