	import React, { useState,useEffect } from 'react';
	import {STLViewer} from 'react-stl-obj-viewer';
	import { makeStyles } from '@material-ui/core/styles';
	import {IconButton, Collapse, Grid, Paper} from '@material-ui/core';
	import BackspaceIcon from '@material-ui/icons/Backspace';
	import { CirclePicker } from 'react-color';



	const useStyles = makeStyles(theme => ({
		display: {
		  flexGrow: 1,
		  marginTop: theme.spacing(3),
		},
		color: {
			marginLeft: theme.spacing(10),
			width: 100
		  },
	  }));

	function STLDisplay(props){

		const classes = useStyles();

		const [aux, setaux] = useState([]);
		const [button, setbutton] = useState(false);
		const [backColor,] = useState("#b0b0b0");
		const [modelColor, setmodelColor ] = useState();

		useEffect(() => {   
			setaux([props.file])
			setbutton(true)
			// eslint-disable-next-line react-hooks/exhaustive-deps
		},[props.file]);

		useEffect(() => {   
			setmodelColor("#ffffff")
		},[modelColor]);

		function deleteid(){
			setaux([])
			setbutton(false)
		}

		function changeHandler(color){
			setmodelColor(color.hex)
		}



	return(
		<div>
      		<Grid container spacing={2} direction="row">

				<Collapse in={button}>
					<IconButton color="primary" aria-label="Display" onClick={deleteid}>
					<BackspaceIcon fontSize="large"/>
					</IconButton>
				</Collapse>
			</Grid>

			<Grid container className={classes.display} direction="row">
				{aux.map(auxModel=>
					<STLViewer
					key={auxModel.id+256}
					width={650}
					height={320}
					sceneClassName="test-scene"
					file={auxModel}
					className="stl"
					modelColor={modelColor}
					backgroundColor={backColor}
					/> 
				)
				}
				{button ? 

					<CirclePicker className={classes.display} width={100} 
						onChange={ changeHandler }
					/> 

					: null
				}
			</Grid>
		</div>
	)};
	export default STLDisplay;
