import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {  Button, Grid, Checkbox} from '@material-ui/core';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import LaunchIcon from '@material-ui/icons/Launch';
import IconButton from '@material-ui/core/IconButton';
import SendIcon from '@material-ui/icons/Send';
import { Alert, AlertTitle } from '@material-ui/lab';
import Collapse from '@material-ui/core/Collapse';
import CloseIcon from '@material-ui/icons/Close';
import Preview from './Preview';
import {Link} from "react-router-dom";
import STLDisplay from './STLDisplay';






const useStyles = makeStyles(theme => ({
  title: {
    flexGrow: 1,
    marginLeft: theme.spacing(8),
  },
  root:{
    marginTop:80,
    flexGrow: 1,
  },
  upload: {
    flexGrow: 1,
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(12),
    marginBottom: theme.spacing(3),
  },
  fullWidth: {
    width: '100%',
  },
  input: {
    display: 'none',
  },
  table: {
    margin: theme.spacing(2),
    maxHeight: 270,
  },
  paper: {
    margin: theme.spacing(2),
  },
  model: {
    marginLeft:theme.spacing(5),
  },
  buttons: {
    marginLeft:theme.spacing(30),
  },


}));




function Upload() {

  const [models, setModels] = useState([]);
  const [aux, setaux] = useState();
  const [count, setCount] = useState(0);
  const [open, setOpen] = React.useState(false);
  const [alert, setAlert] = React.useState(false);
  const [send, setSend] = React.useState(false);
  const useForceUpdate = () => useState()[1];

  const classes = useStyles();

  useEffect(() => {
    // action on update of movies
}, [movies]);

  const addItem = (file) => {
    setModels([
      ...models,
      {
        id: models.length,
        value: file.name,
        sel: false,
        file: file,
      }
    ]);
  };

  function handleCheckboxClick(event, id){
    event.stopPropagation();


      if (models[id].sel === false) {
          models[id].sel = true;
          setCount(count + 1)

      } else if (models[id].sel === true) {
          models[id].sel = false; 
          setCount(count - 1)
      }

  };
  function contain(element){
    for (let m of models) {
      if(m.value === element){
        setAlert(true);
        return true
      }
    }
    return false
}

  function handleUpload(e) {
    var UpModels = Array.from(e.target.files)

    for (let um of UpModels) {
      if(contain(um.name)!==true){
        addItem(um)
      }
    }
  }

  function handleServer(){
    const data = new FormData()
    if(count > 0){

    for (let m of models) {
      if(m.sel === true){
        data.append('files', m.file)
      }
    }
    axios.post("http://localhost:3002/upload",data).then(response => {
      // server answer
    }).catch(e => {
      console.log(e);
    });
    setSend(true)
  }
  }

  function handlerDisplay(){
    if(count === 1){
      for (let m of models) {
        if(m.sel === true){
         setaux(m.file)
        }
      }
    }else{
      setOpen(true);
    }
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={2} direction="row">
        <Grid container item xs={3} direction="column">
          <div>
          <Button variant="contained" component="label" startIcon={<CloudUploadIcon />}className={classes.upload}>
              Upload File
              <input
                  type="file"
                  accept=".obj,.stl"
                  className={classes.input}
                  id="Upload-button-file"
                  onChange={handleUpload.bind(this)}
              />
          </Button>
          </div>
          <TableContainer component={Paper} className={classes.table}>
            <Table  aria-label="simple table" stickyHeader>
              <TableHead>
                <TableRow>
                <TableCell></TableCell>
                  <TableCell>Uploaded files</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {models.map(model =>
                  <TableRow key={model.id}>
                    <TableCell padding="checkbox">
                      <Checkbox className={classes.check} onClick={event => handleCheckboxClick(event, model.id)}/>
                    </TableCell>
                    <TableCell>{model.value}</TableCell>
                    <TableCell>  
                      <Preview file={model.file} size={50}/>
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <Grid container item direction="row" className={classes.buttons} >
          <IconButton color="secondary" aria-label="Display">
             <SendIcon fontSize="large"/>
          </IconButton>
              <IconButton color="primary" aria-label="Display" onClick={handlerDisplay}>
                <LaunchIcon fontSize="large"/>
              </IconButton>
          </Grid>
        </Grid>
        <Grid item xs={9} >
          <Collapse in={alert}>
            <Alert severity="warning"
              action={
                <IconButton
                  aria-label="alert"
                  color="inherit"
                  size="small"
                  onClick={() => {
                    setAlert(false);
                  }}
                >
                  <CloseIcon fontSize="inherit" />
                </IconButton>
              }
            >
              <AlertTitle>Warning</AlertTitle>
                You can not upload 2 files with same name.
            </Alert>
          </Collapse>
          <Collapse in={open}>
            <Alert severity="warning"
              action={
                <IconButton
                  aria-label="alert"
                  color="inherit"
                  size="small"
                  onClick={() => {
                    setOpen(false);
                  }}
                >
                  <CloseIcon fontSize="inherit" />
                </IconButton>
              }
            >
                <AlertTitle>Warning</AlertTitle>
                  You must select only one model to display.
            </Alert>
          </Collapse>
          <Collapse in={send}>
            <Alert severity="success"
              action={
                <IconButton
                  aria-label="alert"
                  color="inherit"
                  size="small"
                  onClick={() => {
                    setSend(false);
                  }}
                >
                  <CloseIcon fontSize="inherit" />
                </IconButton>
              }
            >
                <AlertTitle>Send</AlertTitle>
                  Your models had sended to server successful!
            </Alert>
          </Collapse>
          <div className={classes.model}>
            {aux ?
                <STLDisplay file={aux}/> : null
            }
          </div>
        </Grid>
      </Grid>
    </div>
  );
}



export default Upload;
